INSERT INTO ClimateAgreement (CountryCode, CountryName, AgreementName, DateSigned, Target , TargetYear) VALUES ('CHN', 'China', 'Paris', to_date('2021-10-28 ', 'yyyy-mm-dd'), 'China’s updated NDC contains five overarching targets:1.	Peaking carbon dioxide emissions “before 2030” (up from the previous “around 2030 and making efforts to peak earlier”) and achieve carbon neutrality before 2060.2.	Lower carbon intensity by “over 65%” in 2030 from the 2005 level, (up from the previous “by 60–65%”).3.	Share of non-fossil fuels in primary energy consumption to “around 25%” in 2030, (up from “around 20%”).4.	Increase forest stock volume by around 6 billion cubic metres in 2030 from the 2005 level, (previously 4.5 billion cubic metres).5.	Increase the installed capacity of wind and solar power to over 1,200 GW by 2030 (new target).', 2030);
INSERT INTO ClimateAgreement (CountryCode, CountryName, AgreementName, DateSigned, Target , TargetYear) VALUES ('CHN', 'China', 'Net Zero', to_date('2021-10-28 ', 'yyyy-mm-dd'), 'China will strive to reach a CO2emissions peak before 2030, and achieve carbon neutrality before 2060', 2050);
